mapper.select:
SELECT clist_pdt_count as clistPdtCount
FROM o_cartlist
WHERE user_id = #{userId} AND pdt_id = #{pdtId}

mapper.update:
UPDATE o_cartlist
SET clist_pdt_count = clist_pdt_count + #{pdtCount}
WHERE user_id = #{userId} AND pdt_id = #{pdtId}

UPDATE o_cartlist
SET clist_pdt_count = clist_pdt_count + 1
WHERE clist_id = #{clistId}

